import React from "react"
import {
	Chart as ChartJS,
	CategoryScale,
	LinearScale,
	PointElement,
	LineElement,
	Title,
	Tooltip,
	Legend,
	ArcElement,
} from "chart.js"
import { Line } from "react-chartjs-2"
import styled from "styled-components"
import { useGlobalContext } from "../../context/globalContext"

ChartJS.register(
	CategoryScale,
	LinearScale,
	PointElement,
	LineElement,
	Title,
	Tooltip,
	Legend,
	ArcElement
)

function Chart() {

    const {incomes, expenses}  = useGlobalContext()

    const data = {
        labels: incomes.map
    }

	return (
		<ChartStyled>
			<Line />
		</ChartStyled>
	)
}

const ChartStyled = styled.div`
	background: #fcf6f9;
	border: 2px solid #ffffff;
	box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
	padding: 1rem;
	border-radius: 20px;
	height: 100%;
`

export default Chart
