import React from "react"
import styled from "styled-components"

function Button({ name, icon, onClick, bg, bPad, color, bRadius }) {
	return (
		<ButtonStyled
			style={{
				$bg: bg,
				$bPad: bPad,
				$color: color,
				$bRadius: bRadius,
			}}
			onClick={onClick}>
			{icon}
			{name}
		</ButtonStyled>
	)
}

const ButtonStyled = styled.button`
	outline: none;
	border: none;
	font-family: inherit;
	font-size: inherit;
	display: flex;
	align-items: center;
	gap: 0.5rem;
	cursor: pointer;
	transition: all 0.4s ease-in-out;

	&:hover {
		background: var(--color-green) !important;
		color: #fff !important;
	}
`

export default Button
