import styled from "styled-components"

export const MainLayout = styled.div`
	padding: 2rem;
	height: 100%;
	display: flex;
	gap: 2rem;
	overflow: hidden;

	@media (max-width: 768px) {
		flex-direction: column;
		padding: 1rem;
	}
`

export const InnerLayout = styled.div`
	width: 100%;
	height: 100%;
	overflow: hidden;
	display: flex;
	flex-direction: column;
	gap: 1rem;
`
